    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
                     // TODO add your handling code here:
          
          if(jComboBox1.getSelectedItem()==jComboBox1.getItemAt(0))
          {
              Insert obj=new Insert();
              obj.setVisible(true);
              this.setVisible(false);
          }
          else if(jComboBox1.getSelectedItem()==jComboBox1.getItemAt(1))
          {
              Search obj=new Search();
              obj.setVisible(true);
              this.setVisible(false);
          }
          else if(jComboBox1.getSelectedItem()==jComboBox1.getItemAt(2))
          {
              WordDay obj=new WordDay();
              obj.setVisible(true);
              this.setVisible(false);
          }
          else if(jComboBox1.getSelectedItem()==jComboBox1.getItemAt(3))
          {
              Similar obj=new Similar();
              obj.setVisible(true);
              this.setVisible(false);
          }    
              
    }//GEN-LAST:event_jButton1MouseClicked

